	insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
	('Beyoncé', 'Bakarlaria', "Beyoncé Giselle Knowles-Carter (Houston, 1981) dantzaria, abeslaria, konpositorea eta aktorea XXI. mendeko pop ikono bat da, eta, gainera, senarrarekin batera, Jay-Z rapzale eta produktorea sortzen du, musika garaikidean eragin handiena duen tandemetako bat.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
	('Ed Sheeran', 'Bakarlaria', "Edward Christopher Sheeran, Ed Sheeran, kantautore eta musikari britainiarra, da ezaguna. Txikitatik hasi zen joaten zen elizan kantatzen, eta gitarra jotzen ere ikasi zuen.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
    ('Rosalía', 'Bakarlaria', "Rosalía Espainiako abeslari, konpositore eta ekoizlea da, eta ezaguna da gaur egungo flamenko-soinuak aztertzen dituen musika-estiloagatik. Espainiako musika tradizionala hip-hop, pop, trap eta musika elektronikoa bezalako generoekin nahastuz, soinu berri bat sortu zuen musika esperimentalaren barruan.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
    ('Alejandro Sanz', 'Bakarlaria', "Alejandro Sánchez Pizarro (Madril, 1968ko abenduaren 18a), artistikoki Alejandro Sanz gisa ezaguna, kantautore eta konpositore espainiarra da. Mundu osoan 25 milioi disko baino gehiago saldu ditu eta 24 Grammy Latinos eta 4 Grammy estatubatuar irabazi ditu.");
	insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
	('Pablo Alborán', 'Bakarlaria', "Pablo Moreno de Alborán Ferrándiz, Malagan jaioa, kantautorea eta Español musikaria. Hasieratik lau estudio-album ditu, zuzeneko bi album eta zenbait musika-kolaborazio. Pablo Alborán debut-albumaren lehen Sólo tú jaurtiketa ofizialarekin egin zuen debuta 2010ean, 2011ko otsailean.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
	('Drake', 'Bakarlaria', "Drake Drakeren biografia (Aubrey Drake Graham) hip hop abeslaria da, konpositorea, ekoizlea eta aktore kanadarra. Inoiz izan den hiri-musikako artista arrakastatsuenetako bat da. Rockaren eta poparen konbinazioak milioika euroko salmentak izan ditu mundu osoan.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
    ('David Bisbal', 'Bakarlaria', "David Bisbal Ferre (Almeria, 1979ko ekainaren 5a) abeslari, konpositore eta aktore espainiarra da, eta 2000ko hamarkadaren hasieran ospetsu egin zen, Operación Triunfo 2001.2 saioko bigarren finalista geratu baitzen.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
	('Eminem', 'Bakarlaria', "Marshall Bruce Mathers III (1972ko urriaren 17a, St. Joseph, Missouri), bere izen artistikoagatik ezagunagoa, Eminem abeslari eta konpositore iparramerikarra da, raparen historiako izar nagusietako bat. Eminem familia desegituratu batean jaio zen.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
	('Coldplay', 'taldea', "Coldplay pop rock alternatiboa duen talde britainiarra da, eta Londresen sortu zen 1996an. Oasis, U2, Radiohead, Travis eta beste talde batzuekin alderatuz gero, taldea azken hamarkadetako musikaren panoramako garrantzitsuenetakoa da, saldutako 100 milioi kopia baino gehiago.");
    insert into musikaria (izenArtistikoa, ezaugarria, deskribapena) values 
